import React from "react";
import BeigeCard from "../../layout/card/BeigeCard";
import { connect } from "react-redux";
import { restaurant } from "../../../store/action";
import "./AllRestaurants.scss";
import { Link } from "react-router-dom";

const RestaurantsGrid: React.FC<{ addRestaurantData: any}> = () => {
  return (
    <div className="restaurants-grid">
      {restaurant.payload.map((item) => {
        return (
          <Link
            to={`/RestaurantPage/${item}`}
            className="to-restaurant-btn"
          >
            {/* <BeigeCard
              key={item.name}
              title={item.name}
              detail={item.name}
              img={item.name}
            ></BeigeCard> */}
          </Link>
        );
      })}
    </div>
  );
};

const mapStateToProps = (state: { data: any }) => ({ data: state.data });

const mapDispatchToProps = (
  dispatch: (arg0: { type: string; payload: any }) => any
) => {
  return {
    addData: () => dispatch(addRestaurantData),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(RestaurantsGrid);
