import React from "react";
import BeigeCard from "../../layout/card/BeigeCard";
import { useSelector } from "react-redux";
import "./AllRestaurants.scss";
import { Link } from "react-router-dom";
import { useAppDispatch, useAppSelector } from "../../../store/store";
import { useEffect } from "react";
import { fetchDishes } from "../../../store/dishDataSlice";

const RestaurantsGrid = () => { 
  const dispatch= useAppDispatch();
  const {dishes}= useSelector((state:any)=> state.dishes);

  useEffect(() => {
    dispatch(fetchDishes()) 
    }, []);

  return (
    <div className="restaurants-grid">

       {dishes.map((item: any) => {
        return (
          <Link
            to={`/RestaurantPage/${item}`}
            className="to-restaurant-btn"
          >
            <BeigeCard
              key={item.name}
              title={item.name}
              detail={item.name}
              img={item.mobileImage}
            ></BeigeCard> 
         </Link>
       );
      })} 
    </div>
  );
};
export default RestaurantsGrid
