import { createStore } from "redux";

const initailState = {
  data: "",
};

const rootReducer = (state = initailState, action) => {
  switch (action.type) {
    case "ADD_RESTAURANT_DATA":
      return {
        ...state,
        data: action.payload,
      };
    case "ADD_DISH_DATA":
      return {
        ...state,
        data: action.payload,
      };
      case "ADD_CHEF_DATA":
        return {
          ...state,
          data: action.payload,
        };  
    default:
      return state;
  }
};

export default createStore(rootReducer);
