import { useState } from "react";

const ChefItem = ({ chef }) => {
  const [yossiArr, setYossiArr]= useState([]);
  if(chef.chefName=== "Yossi Shitrit") {
    for(var i=0; i<chef.restaurantName.length; i++) {
      setYossiArr(yossiArr).push({name: chef.restaurantName[i], img: chef.restaurantImage[i]})
    }
  }

  return (
    <div>
      {yossiArr.map((restaurant) => {
        <div className="chef-restaurant-item">
          <img
            src={restaurant.name}
            alt="img"
            className="chef-restaurant-img"
          />{" "}
          <h3>{restaurant.img}</h3>
        </div>;
      })}
      ;
    </div>
  );
};

export default ChefItem;
