import Input from "../layout/form/Input";
import "./User.scss";
import { Link } from "react-router-dom";
import { Fragment, useEffect, useState } from "react";
import Footer from "../footer/Footer";
import Header from "../header/Header";
import SubmitButton from "../layout/button/SubmitButton";
import axios from "axios";


const SignIn = () => {

  const [inputs, setInputs] = useState({})
  const [feedback, setFeedback] = useState("")

  const handleChange = (e: React.FormEvent<HTMLInputElement>) => {
    setInputs({...inputs, [e.target.name]: e.target.value})
  }

  const handleSubmit = async (e: React.FormEvent<HTMLInputElement>)=> {
    e.preventDefault()
    try {
    const result = await axios.post("http://localhost:8080/auth/signin", inputs)
    if(result.data.token) localStorage.setItem("token", result.data.token)
    setFeedback("Logged in successfuly")
    }
    catch(err: any) {
      setFeedback(err.response.data || "Network error. try again")
    }
  }

  const isLoggedIn = async () => {
    const token: any = localStorage.getItem("token")
    try {
    const result = await axios.get("http://localhost:8080/auth/", {
      headers: {
        "auth-token": token
      }
    })
    document.location.href = "http://localhost:3000/"
  } catch(err) {
    localStorage.removeItem("token")
  }
  }

  useEffect(() => {
    const token = localStorage.getItem("token")
    if(token) isLoggedIn()
  }, [])

  return (
    <Fragment>
      <Header />
      <form>
        <h2>Sign In</h2>
        <Input
          content={"Username "}
          placeholder={"Please Enter Your Username"}
          type={"text"}
          onChange={(e:any) => handleChange(e)}
        ></Input>
        <Input
          content={"Password "}
          placeholder={"Please Enter Your Password"}
          type={"password"}
          onChange={(e:any) => handleChange(e)}
        ></Input>
        <SubmitButton />
        <p>
          Don't have an account yet? <Link to="/signup">Sign Up</Link>
        </p>
        <b>{feedback}</b>
      </form>
      <Footer />
    </Fragment>
  );
};

export default SignIn;
