import SignatureItem from "./SignatureItem";
import "./HomePage.scss";


const SignatureDish= ({data}) => {
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [{breakpoint: 1300,settings: {slidesToShow: 2,},},{breakpoint: 1050,settings: {slidesToShow: 1.1,},},]
  };
  const smallerArr= data.slice(0,3);
  return (
    <div className="slider-div">
      <Slider {...settings}>
        {smallerArr && smallerArr.map((dish) => {
          return <SignatureItem dish={dish}></SignatureItem>
        })}
      </Slider>
    </div>
  );
      }


export default SignatureDish;
