import { Fragment, useEffect, useState } from 'react';
import Input from '../layout/form/Input';
import './User.scss';
import { Link } from 'react-router-dom';
import Header from '../header/Header';
import Footer from '../footer/Footer';
import SubmitButton from '../layout/button/SubmitButton';
import axios from 'axios';
const SignUp = () => {
  const privacyLink= <Link to="/PrivacyPolicy"> Terms  Privacy.</Link>;

  const [inputs, setInputs] = useState({})
  const [feedback, setFeedback] = useState("")

  const handleChange = (e: any) => {
    setInputs({...inputs, [e.target.name]: e.target.value})
  }

  const handleSubmit = async (e: any)=> {
    e.preventDefault()
    try {
    const result = await axios.post("http://localhost:8080/auth/signup", inputs)
    if(result.data.token) localStorage.setItem("token", result.data.token)
    setFeedback("Signed up successfuly")
    }
    catch(err: any) {
      setFeedback(err.response.data || "Network error. try again")
    }
  }

  const isLoggedIn = async () => {
    const tokemn = localStorage.getItem("token")
    try {
    const result = await axios.get("http://localhost:8080/auth/", {
      headers: {
        "auth-token": token
      }
    })
    document.location.href = "http://localhost:3000/"
  } catch(err) {
    localStorage.removeItem("token")
  }
  }

  useEffect(() => {
    const token = localStorage.getItem("token")
    if(token) isLoggedIn()
  }, [])
  return (
    <Fragment>
      <Header />
        <form>
        <h2>Sign Up</h2>
        <p>Please fill in this form to create an account.</p>
                <Input content={'First Name '} placeholder={'Please Enter Your First Name'} type={'text'} ></Input>
                <Input content={'Last Name '} placeholder={'Please Enter Your Last Email'} type={'text'} ></Input>                
                <Input content={'Username '} placeholder={'Please Enter Your Username'} type={'text'} ></Input>
                <Input content={'Password '} placeholder={'Please Enter Your Password'} type={'password'} ></Input>
                <Input content={'Confirm Password '} placeholder={'Please Confirm Your Password'} type={'password'} ></Input>
                <Input content={'By creating an account you agree to our Terms & Privacy.'} placeholder={''} type={'checkbox'}></Input>
                <p> </p>
                <SubmitButton />
                <p>Already have an account?  <Link to='/signin'>Sign In</Link></p>
        </form>
        <Footer />
    </Fragment>
  )
}

export default SignUp;
